import React from 'react';
import Link from 'next/link';

export default function Hero(): JSX.Element {
  return (
    <section className="font-mono">
      <div className="container mx-auto flex px-5 py-40 md:flex-row flex-col items-center">
        <div className="lg:max-w-lg lg:w-full md:w-1/2 w-5/6 mb-10 md:mb-0">
          <img src='/jenil.svg' alt="logo"/>
        </div>
        <div className="lg:flex-grow md:w-1/2 lg:pl-24 md:pl-16 flex flex-col md:items-start md:text-left items-center text-center">
          <h1 className="title-font sm:text-4xl text-3xl mb-4 font-medium text-black font-monob">Hi, I'm Jenil 
      </h1>
          <p className="mb-8 leading-relaxed text-black text-xl">I like to build software that contributes towards human progress. I'm currently working on <Link href="https://coinvise.co"><a className="underline font-monob">Coinvise</a></Link>, infrastructure for web3 tokenized creators and communities. Occasionally, I like to write about crypto <Link href="https://jenil.substack.com"><a className="underline font-monob">here</a></Link>.</p>
          <p className="leading-relaxed text-black text-xl mb-8">I also invest in DAOs. If you're building one, feel free to reach out to me on twitter or read my memo for founders.</p>
          <p className="leading-relaxed text-black text-xl">We're also hiring senior fullstack devs & smart contract developers. If you'd like to join our team, reach out to me below.</p>
        </div>
      </div>
    </section>
  );
}
