import React from 'react';
import Link from 'next/link';

export default function Hero(): JSX.Element {
  return (
    <section className="font-mono">
      <div className="container mx-auto flex px-5 py-40 md:flex-row flex-col items-center">
        <div className="lg:max-w-lg lg:w-full md:w-1/2 w-5/6 mb-10 md:mb-0">
          <img src='/jenil.png' alt="logo" className="" />
        </div>
        <div className="lg:flex-grow md:w-1/2 lg:pl-24 md:pl-16 flex flex-col md:items-start md:text-left items-center text-center">
          <h1 className="title-font sm:text-4xl text-3xl mb-4 font-medium text-black font-monob">building the future
        <br className="hidden lg:inline-block" />of humanity
      </h1>
          <p className="leading-relaxed text-black text-xl">I'm Jenil, I like to build software that contributes toward human progress. I'm currently working on <Link href="https://coinvise.co"><a className="underline font-monob">Coinvise</a></Link> and <Link href="https://autoviseai.com"><a className="underline font-monob">AutoviseAI</a></Link>.</p>
        </div>
      </div>
    </section>
  );
}
